import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart, incrementCartItem, decrementCartItem } from '../../redux/actions/action'; // Импортируем новые действия

const Cart = () => {
    const [totalPrice, setTotalPrice] = useState(0);
    const carts = useSelector(state => state.cartReducer.carts);
    const dispatch = useDispatch();

    const handleRemoveFromCart = (isbn) => { // Изменяем аргумент на isbn
        dispatch(removeFromCart(isbn)); // Используем новое действие removeFromCart
    };

    const handleIncrementQuantity = (isbn) => {
        dispatch(incrementCartItem(isbn)); // Используем новое действие incrementCartItem
    };

    const handleDecrementQuantity = (isbn) => {
        dispatch(decrementCartItem(isbn)); // Используем новое действие decrementCartItem
    };

    useEffect(() => {
        let total = 0;
        carts.forEach(item => {
            if (item.price && item.quantity) {
                total += (item.price * item.quantity);
            }
        });
        setTotalPrice(total);
    }, [carts]);

    return (
        <div>
            <h2>Shopping Cart</h2>
            {carts.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <div>
                    {carts.map(item => (
                        <div key={item.isbn}>
                            <p>{item.title} - ${item.price}</p>
                            {/* добавить картинку */}
                            <img src={item.image} alt={item.title} />


                            <p>Quantity: {item.quantity}</p>
                            <button onClick={() => handleRemoveFromCart(item.isbn)}>Remove</button>
                            <button onClick={() => handleDecrementQuantity(item.isbn)}>Decrease</button> {/* Добавляем кнопку для уменьшения количества */}
                            <button onClick={() => handleIncrementQuantity(item.isbn)}>Increase</button> {/* Добавляем кнопку для увеличения количества */}
                        </div>
                    ))}
                    <p>Total Price: ${isNaN(totalPrice) ? 0 : totalPrice}</p>
                </div>
            )}
        </div>
    );
};

export default Cart;
