const INIT_STATE = {
    carts: []
}

export const cartReducer = (state = INIT_STATE, action) => {
    switch (action.type) {
        case "ADD_TO_CART":
            return {
                ...state,
                carts: [...state.carts, action.payload]
            }
        case "REMOVE_TO_CART":
            const updatedCarts = state.carts.filter((item) => item.isbn !== action.payload);
            return {
                ...state,
                carts: updatedCarts
            }
        case "UPDATE_CART_QUANTITY":
            const { isbn, quantity } = action.payload;
            const updatedCart = state.carts.map(item => {
                if (item.isbn === isbn) {
                    return {
                        ...item,
                        quantity: quantity
                    };
                }
                return item;
            });
            return {
                ...state,
                carts: updatedCart
            };
        default:
            return state;
    }
}
