const INIT_STATE = {
    carts: []
}
export const cartReducer = (state = INIT_STATE, action) => {
    switch (action.type) {
    case ADD_TO_CART:
      const { userId, book } = action.payload;
      const existingCart = state.carts[userId] || [];
      return {
        ...state,
        carts: {
          ...state.carts,
          [userId]: [...existingCart, { ...book, quantity: 1 }],
        },
      };


        case "UPDATE_CART_QUANTITY":
            const itemIndex = state.carts.findIndex((item) => item.isbn === action.payload.isbn);
            if (itemIndex !== -1) {
                state.carts[itemIndex].quantity = action.payload.quantity
                return {
                    ...state
                }
            }
            return state;



        default:
            return state;
    }
}
