// Product.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Spinner from 'react-bootstrap/Spinner';
import { useDispatch } from 'react-redux';
import { ADD } from '../redux/actions/action';
import  axios  from 'axios';

const Product = () => {
  const dispatch = useDispatch();
  const { isbn } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  const send = (item) => {
    dispatch(ADD(item));
    alert("Item added successfully");
  };

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:8888/search?query=${isbn}`);
        const data = await response.json();
        setProduct(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching product:', error);
        setLoading(false);
      }
    };

    fetchProduct();
  }, [isbn]);

  if (loading) return <Spinner animation="border" />;

  return (
    <div className="container">

      {/* {product ? (
        <div className="row">
          <div className="col-lg-6 mt-5">
            <img src={product.image} alt={product.title} height={400} width={800} />
          </div>
          <div className="col-lg-6 mt-5">
            <h4 className='text-uppercase'>{product.category}</h4>
            <h1 className='display-5'>{product.title}</h1>
            <p className='fw-bolder'>Rating: {product.rating && product.rating.rate}</p>
            <h3>$ {product.price}</h3>
            <p>{product.description}</p>
            <Button onClick={() => send(product)} variant="dark">Add to Cart</Button>
            <Button className='ms-3' variant="dark">Go to Cart</Button>
          </div>
        </div>
      ) : (
        <p>Product not found.</p>
      )} */}
    </div>
  );
};

export default Product;
