export const cartReducer = (state = INITIAL_STATE, action) => {
    const { userId, bookId, quantity } = action.payload; // Деструктуризация userId, bookId, quantity

    switch (action.type) {
      case ADD_TO_CART:
        const existingCart = state.carts[userId] || [];
        const updatedCartAdd = [
          ...existingCart,
          { bookId, quantity: 1 }, // Используем bookId и начальное количество
        ];
        return {
          ...state,
          carts: {
            ...state.carts,
            [userId]: updatedCartAdd,
          },
        };

      case REMOVE_FROM_CART:
        const updatedCartRemove = state.carts[userId].filter(
          (item) => item.bookId !== bookId
        );
        return {
          ...state,
          carts: {
            ...state.carts,
            [userId]: updatedCartRemove,
          },
        };

      case UPDATE_CART_QUANTITY:
        const updatedItems = state.carts[userId].map((item) =>
          item.bookId === bookId ? { ...item, quantity } : item
        );
        return {
          ...state,
          carts: {
            ...state.carts,
            [userId]: updatedItems,
          },
        };

      default:
        return state;
    }
  };
