import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addToCart, removeFromCart, updateCartQuantity } from '../../redux/actions/action';

const Cart = () => {
  const [totalPrice, setTotalPrice] = useState(0);
  const carts = useSelector(state => state.cartReducer.carts);
  const dispatch = useDispatch();

  const handleRemoveFromCart = (id) => {
    dispatch(removeFromCart(id));
  };

  const handleUpdateCartQuantity = (item, quantity) => {
    dispatch(updateCartQuantity(item, quantity));
  };

  useState(() => {
    let total = 0;
    carts.forEach(item => {
      total += (item.price * item.quantity);
    });
    setTotalPrice(total);
  }, [carts]);

  return (
    <div>
      <h2>Shopping Cart</h2>
      {carts.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div>
          {carts.map(item => (
            <div key={item.isbn}>
              <p>{item.title} - ${item.price}</p>
              <p>Quantity: {item.quantity}</p>
              <button onClick={() => handleRemoveFromCart(item.isbn)}>Remove</button>
              <input
                type="number"
                value={item.quantity}
                onChange={(e) => handleUpdateCartQuantity(item, parseInt(e.target.value))}
              />
            </div>
          ))}
          <p>Total Price: ${totalPrice}</p>
        </div>
      )}

      <h2>Add Item to Cart</h2>
   
    </div>
  );
};



export default Cart;
