import {
  SEARCH_BOOKS_REQUEST,
  SEARCH_BOOKS_SUCCESS,
  SEARCH_BOOKS_FAILURE,
} from '../actions/action';

const initialState = {
  searchTerm: '',
  searchResults: [],
  loading: false,
  error: null,
};

const searchReducer = (state = initialState, action) => {
  switch (action.type) {
    case SEARCH_BOOKS_REQUEST:
      return {
        ...state,
        searchTerm: action.payload, // Обновляем текущий поисковый запрос
        loading: true,
        error: null,
      };
    case SEARCH_BOOKS_SUCCESS:
      // Проверяем, чтобы результаты поиска относились к текущему поисковому запросу
      if (action.payload.searchTerm === state.searchTerm) {
        return {
          ...state,
          searchResults: action.payload.results,
          loading: false,
          error: null,
        };
      }
      return state; // Если запрос устарел, не обновляем состояние
    case SEARCH_BOOKS_FAILURE:
      return {
        ...state,
        loading: false,
        error: action.payload,
      };
    default:
      return state;
  }
};

export default searchReducer;
