import React from 'react'
import { useSelector } from 'react-redux';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { Link } from 'react-router-dom';
import Logo from '../Logo/Logo'
import '../Header/'



 const Header = () => {
    const getData = useSelector((state) => state.cartReducer.carts)

    return (
    <header className='header'>


                <Link style={{ textDecoration: 'none' }} to='/'>
               <div className='logo'>
                    <Logo />
                </div>


                </Link>
                <Nav className="me-auto">

                        <Link style={{ textDecoration: 'none' }} to="/">Home</Link>


                        <Link style={{ textDecoration: 'none' }} to='/products'>All Books</Link>


                        <Link style={{ textDecoration: 'none' }} to='/contacts'>Our Contacts</Link>


                </Nav>
                <div className="buttons">

            <button>
                <i className='fa fa-shopping-cart me-2'></i>
                <Link to="/cart">{getData.length}</Link> 
            </button>

        <button><i className='login'></i>Login</button>
        <button ><i className='register'></i>Register</button>
    </div>


    </header>
    )
    }

    export default Header;
