const INIT_STATE = {
    carts: []
}
export const cartReducer = (state = INIT_STATE, action) => {
    switch (action.type) {
        case "ADD_TO_CART":
            return {
                ...state, carts: [...state.carts, action.payload]
            }
    //   case "REMOVE_TO_CART":
    //     const data=state.carts.filter((item)=>item.isbn!== action.payload)
    //     return{
    //         ...state,
    //         carts:data
    //     }
    case "REMOVE_FROM_CART":
      const updatedStateDecrement = state.map((item) => {
        if (item.isbn === action.payload) {
          const updatedItem = { ...item };
          updatedItem.count = Math.max((item.count || 1) - 1);
          return updatedItem;
        }
        return item;
      });
      return {
       ...state,
        carts: updatedStateDecrement,
      };
        default:
            return state;
    }
}
