import Banner from '../components/Banner/Banner'
     
        import React, { useState, useEffect } from 'react';
        import axios from 'axios';
        import { Link } from 'react-router-dom';
        import styles from '../components/ProductsComponent/ProductsComponent.module.css';
        import { useDispatch } from 'react-redux';
        import { addToCart } from '../../src/redux/actions/action';
        import { BASE_URL } from '../config';
        
const HomePage = () => {
  const [randomBooks, setRandomBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(BASE_URL);
        const data = response.data.map(bookString => JSON.parse(bookString));
        const shuffledBooks = data.sort(() => 0.5 - Math.random());
        const randomBooks = shuffledBooks.slice(0, 8);
        setRandomBooks(randomBooks);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleAddToCart = (item) => {
    dispatch(addToCart(item));
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <>
    <Banner/>
    <div className={styles['product-list-container']}>
      <h2 style={{ textAlign: 'center' }}>Discover our Best Sellers</h2>
      <div className={styles['book-container']} >
        {randomBooks.map((book) => (
          <div key={book.isbn} className={styles['book']}>
            <Link to={`/books/${book.isbn}`}>
              <img
                src={`http://127.0.0.1:8080/images/${book.image_name}`}
                alt={book.title}
              />
              <h3>{book.title.length > 15 ? `${book.title.substring(0, 30)}...` : book.title}</h3>
            </Link>
            <div>
              <p>Author: {book.author}</p>
              <p>Category: {book.category}</p>
              <p>Pages: {book.pages}</p>
              <p>ISBN: {book.isbn}</p>
            </div>
            <button onClick={() => handleAddToCart(book)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
    </>
  );
};



export default HomePage
