// Product.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Spinner from 'react-bootstrap/Spinner';
import { useDispatch } from 'react-redux';
import { addToCart, removeFromCart } from '../redux/actions/action';
import axios from 'axios';

const Product = ({item, userId}) => {
  const dispatch = useDispatch();
  const { isbn } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:8888/search?query=${isbn}`);
        const data = response.data.map((bookString) => JSON.parse(bookString)); // Assuming response is an array of strings
        const selectedProduct = data.find((book) => book.isbn === isbn); // Find the product matching the provided ISBN
        setProduct(selectedProduct);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching product:', error);
        setLoading(false);
      }
    };

    fetchProduct();
  }, [isbn]);

  const handleAddToCart = () => {
    dispatch(addToCart(isn, userId));
};

const handleRemoveFromCart = () => {
    dispatch(removeFromCart(userId, item.isbn));
};
  if (loading) return <Spinner animation="border" />;

  return (
    <div className="container">
      {product ? (
        <div className="row">
          <div className="col-lg-6 mt-5">
            <img src={`http://127.0.0.1:8888/images/${product.image_name}`} 
            alt={product.title} height={1000} width={800} margin={200}/>
          </div>
          <div className="col-lg-6 mt-5">
            <h4 className='text-uppercase'>{product.category}</h4>
            <h1 className='display-5'>{product.title}</h1>
            <p className='fw-bolder'>Pages: {product.pages}</p>
            <h3> {product.author}</h3>
            <p>{product.description}</p>
           <button onClick={handleAddToCart}>Add to Cart</button>
           <button onClick={handleRemoveFromCart}>Remove from Cart</button>

          </div>
        </div>
      ) : (
        <p>Product not found.</p>
      )}
    </div>
  );
};

export default Product;
