const INIT_STATE = {
    carts: []
}

export const cartReducer = (state = INIT_STATE, action) => {
    switch (action.type) {
        case "ADD_TO_CART":
            return {
                ...state,
                carts: [...state.carts, action.payload]
            }
        case "REMOVE_TO_CART":
            const updatedCarts = state.carts.filter((item) => item.isbn !== action.payload);
            return {
                ...state,
                carts: updatedCarts
            }
            case "CART_INCREMENT":
                const updatedStateIncrement = state.map((item) =>
                  item.id === action.payload && item.count !== undefined
                    ? { ...item, count: (item.count || 1) + 1 }
                    : item
                );

                return updatedStateIncrement;
              case "CART_DECREMENT":
                const updatedStateDecrement = state.map((item) => {
                  if (item.id === action.payload) {
                    const updatedItem = { ...item };
                    updatedItem.count = Math.max((item.count || 1) - 1);
                    return updatedItem;
                  }
                  return item;
                });

                return updatedStateDecrement;

        default:
            return state;
    }
}
