const INIT_STATE = {
    carts: {} // Используем объект для хранения корзин пользователей
  };

  export const cartReducer = (state = INIT_STATE, action) => {
    switch (action.type) {
      case "ADD_TO_CART":
        const {  item } = action.payload;
        return {
          ...state,
          carts: {
            ...state.carts,
            [userId]: [...(state.carts[userId] || []), item]
          }
        };

      case "REMOVE_FROM_CART":
        const { userId, itemId } = action.payload;
        const updatedCart = (state.carts[userId] || []).filter(
          (item) => item.isbn !== itemId
        );
        return {
          ...state,
          carts: {
            ...state.carts,
            [userId]: updatedCart
          }
        };

      default:
        return state;
    }
  };
