const INIT_STATE = {
    carts: []
};

export const cartReducer = (state = INIT_STATE, action) => {
    switch (action.type) {
        case "ADD_TO_CART":
            return {
                ...state,
                carts: [...state.carts, action.payload]
            };
        case "REMOVE_TO_CART":
            const updatedCarts = state.carts.filter((item) => item.isbn !== action.payload);
            return {
                ...state,
                carts: updatedCarts
            };
        case "CART_INCREMENT":
            return {
                ...state,
                carts: state.carts.map((item) =>
                    item.isbn === action.payload && item.quantity !== action
                        ? { ...item, quantity: (item.quantity || 1) + 1 }
                        : item
                )
            };
        case "CART_DECREMENT":
            return {
                ...state,
                carts: state.carts.map((item) =>
                    item.isbn === action.payload && item.quantity !== undefined
                        ? { ...item, quantity: Math.max((item.quantity || 1) - 1, 0) }
                        : item
                )
            };
        default:
            return state;
    }
};
