// authReducer.js

const INIT_STATE = {
    isAuthenticated: false,
    userId: null,
    username: null
  };
  
  const authReducer = (state = INIT_STATE, action) => {
    switch (action.type) {
      case 'LOGIN_SUCCESS':
        return {
          ...state,
          isAuthenticated: true,
          userId: action.payload.userId,
          username: action.payload.username
        };
      case 'LOGOUT':
        return INIT_STATE;
      default:
        return state;
    }
  };
  
  export default authReducer;
