import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
// import {
//   searchBooksRequest,
//   searchBooksSuccess,
//   searchBooksFailure,
// } from './redux/actions';
import axios from 'axios';
import { searchBooksRequest, searchBooksSuccess,  } from '../redux/actions/action';

const BookSearch = () => {
  const dispatch = useDispatch();
  const { searchResults, loading, error } = useSelector((state) => state.search);
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = async (term) => {
    dispatch(searchBooksRequest());

    try {
      const response = await axios.get(`http://127.0.0.1:8888/search?query=${term}`);
      const books = response.data.map((bookString) => JSON.parse(bookString));
      dispatch(searchBooksSuccess(books));
    } catch (error) {
      dispatch((error.message));
    }
  };

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleSearch(searchTerm);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={searchTerm}
          onChange={handleChange}
          placeholder="Поиск по названию книги..."
        />
        <button type="submit">Искать</button>
      </form>

      {loading && <p>Loading...</p>}
      {error && <p>Ошибка: {error}</p>}

      <div className="book-container">
        {searchResults.map((book) => (
          <div key={book.isbn} className="book">
            <img
              src={`http://127.0.0.1:8888/images/${book.image_name}`}
              alt={book.title}
            />
            <div>
              <h3>{book.title}</h3>
              <p><strong>Автор:</strong> {book.author}</p>
              <p><strong>Категория:</strong> {book.category}</p>
              <p><strong>Страницы:</strong> {book.pages}</p>
              <p><strong>ISBN:</strong> {book.isbn}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookSearch;
