// ProductList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const ProductList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8888/search?query=Technology');
        const data = response.data.map(bookString => JSON.parse(bookString));
        setBooks(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h2>Books</h2>
      <div className="book-container">
        {books.map((book) => (
          <Link to={`/books/$isbn}`} key={book.isbn} className="book">
            <img
              src={`http://127.0.0.1:8888/images/${book.image_name}`}
              alt={book.title}
            />
            <div>
              <h3>{book.title}</h3>
              <p><strong>Author:</strong> {book.author}</p>
              <p><strong>Category:</strong> {book.category}</p>
              <p><strong>Pages:</strong> {book.pages}</p>
              <p><strong>ISBN:</strong> {book.isbn}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
