import { ADD_TO_CART, REMOVE_FROM_CART } from '../actions/action';

const initialState = {
    userCarts: {} // Объект корзин пользователей
};

const cartReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_TO_CART:
            const { item, userId } = action.payload;
            return {
                ...state,
                userCarts: {
                    ...state.userCarts,
                    [userId]: [...(state.userCarts[userId] || []), item]
                }
            };

        case REMOVE_TO_CART:
            const {  itemId } = action.payload;
            const updatedCart = (state.userCarts[userId] || []).filter(
                (item) => item.isbn !== itemId
            );
            return {
                ...state,
                userCarts: {
                    ...state.userCarts,
                    [userId]: updatedCart
                }
            };

        default:
            return state;
    }
};

export default cartReducer;
